import React from 'react';
import TextField from '@mui/material/TextField';
import './login.css';
import { Button } from '@mui/material';
import { Link } from 'react-router-dom';
import { createTheme, ThemeProvider } from '@mui/material/styles';
import { useState } from 'react';
import axios from 'axios';
import { LOGIN_URL } from '../API/config';

const theme = createTheme({
  palette: {
    primary: {
      main: "#8DD3BB",
    },
  },
});

function Login() {
  const history = useNavigate();
  const [name,setName] = useState("");
  const [email,setEmail] = useState("");
  const [password,setPassword]=useState("");
  const [role,setRole]=useState("");
  
  const sendRequest = async () => {
    const res = await axios.post(`${API_URL}/signup`,{name:name,email:email,password:password,role:role,})
      .catch((err) => console.log(err));
    const data = await res.data;
    return data;
  };
  const handleSubmit = (event)=>{
    event.preventDefault();
    sendRequest().then(() => history("/login"));
  };
  return (

    <div className="main">
        <div className="box" style={{ padding:"150px"}}>
                        <h1>Login</h1>
                        <p></p>
                        <TextField inputProps={{ sx: {width: '300px'} }}  id="outlined-basic" label="Email" variant="outlined" onChange={(e)=>setEmail(e.target.value)}/>
                        <p></p>
                        <TextField inputProps={{ sx: {width: '300px'} }} id="outlined-basic" label="Password" variant="outlined" onChange={(e)=>setPassword(e.target.value)}/>
                        <p></p>
                        <ThemeProvider theme={theme}>
                        <Button  style={{width: '329px',fontSize:"14px",fontWeight:'600',padding:'12px'}}variant="contained" onClick={onLogin}>Login</Button>
                        </ThemeProvider>          
                        <Link to="/signup"><div  style={{width:"225px",justifyContent:"center"}}><h4>If already not a user, SignUp.</h4></div></Link>
            </div>
    </div>
  )
}

export default Login
