import React, { useContext, useEffect, useState } from "react";
import './generalPage.css';
import styled from 'styled-components';
import Nav from '../components/nav';
import ThumbUpIcon from '@mui/icons-material/ThumbUp';
import CommentIcon from '@mui/icons-material/Comment';
import { GRIEVANCE_URL } from '../API/config';
import axios from "axios";

import moment from 'moment';

const Container = styled.div`
  background: #ffffff;
  display: flex;
  justify-content:center; // 1
  flex-flow: column wrap; // 2
  width: 80%;
  height: 100%;
  padding-left:300px;
`;
const List = styled.div`
  display: flex;
  justify-content: center; // 3
  flex-flow: row wrap; // 4
`;

const Card = styled.div`
  margin: 20px;
  background: #fff;
  height: 400px;
  width: 1000px;
  border-radius: 20px;
  box-shadow: 0 10px 20px rgba(0, 0, 0, 0.25);
  display: flex;
  flex-flow: column; // 5 
  padding:20px;
`;


export default function GeneralPage(){
  const[grievance,setGrievance]=useState();

  const sendrequest = async ()=>{
    const res = await axios.get(`${GRIEVANCE_URL}`).catch((err)=>console.log(err));
    const data =await res.data;
    return data;
  }

  useEffect(()=>{
    sendrequest().then((data)=>setGrievance(data));
  },[]);

  console.log(grievance);
  const updateApproval =(id) =>{
    axios.put(`${ADMIN_URL}/updateapproval`,{
      id: id,
      newStatus:"approved",
    })
  }
  return (
    <div className="general">
      <div className="bar"><h className='logo'>Grievance</h></div>
      <Nav />
      <Container>
      <List>
      {grievance && grievance.map((grievance,index) =>(
        <Card>
          <div style={{ position:"relative"}}>
            <table>
              <tr>
                <td><h2><i>{grievance.title}</i></h2>
                </td>
                <td style={{justifyContent:"right",paddingLeft:"20px"}}>
                  <h4 style={{color:"salmon"}}>- {grievance.userId}</h4>
                </td>
                <td style={{justifyContent:"right",paddingLeft:"20px"}}>
                  <h4 style={{color:"salmon"}}>{grievance.progress}</h4>
                </td>
              </tr>
            </table>
            
            <p style={{fontSize:"17px",fontWeight:"500"}}>{grievance.body}</p>
            {/* <CardMedia
          component="img"
      height={130}
      
          image="http://thewowstyle.com/wp-content/uploads/2015/01/nature-wallpaper-27.jpg"
          alt="Paella dish"
        /> */}
           
            <div style={{padding:""}}>
              <table style={{color:"salmon",fontWeight:"600",fontSize:"16.5px"}}>
                <tr>
                  <td>
                     <p onClick={handleVotes} style={{width:"150px",display:"flex"}}><div style={{marginRight:"5px"}}><ThumbUpIcon></ThumbUpIcon></div>{grievance.votes} Votes</p>
                  </td>
                  <td>
                      <p style={{width:"200px",display:"flex"}}><div style={{marginRight:"5px"}}><CommentIcon></CommentIcon></div>Comments</p>
                  </td>
                  <td>
                  <p style={{ marginLeft:"550px"}}>{moment(grievance.createdAt).fromNow()}</p>
                  </td>
                </tr>
              </table>
              <hr style={{border:"0.5px solid #FA8072"}}/>
              <div style={{lineHeight:"0.5",width:"1000px"}}>
                <h5>Omar Siphon</h5>
                <p style={{fontSize:"14px",fontWeight:"500"}}>I feel the same.</p>
                
              </div>
            </div>
            </div>
        </Card>
        ))}
      </List>
    </Container>
    </div>
  );
}

