import React, { useState } from 'react';
import TextField from '@mui/material/TextField';
import './login.css';
import { Button } from '@mui/material';
import { createTheme, ThemeProvider } from '@mui/material/styles';
import {MenuItem,InputLabel,Select,FormControl} from '@mui/material';
import axios from "axios";
import { useNavigate } from 'react-router-dom';
import { API_URL } from '../API/config';

const theme = createTheme({
  palette: {
    primary: {
      main: "#8DD3BB",
    },
  },
});


function Signup() {
  const history = useNavigate();
  const [name,setName] = useState("");
  const [email,setEmail] = useState("");
  const [password,setPassword]=useState("");
  const [role,setRole]=useState("");
  
  const sendRequest = async () => {
    const res = await axios.post(`${API_URL}/signup`,{name:name,email:email,password:password,role:role,})
      .catch((err) => console.log(err));
    const data = await res.data;
    return data;
  };
  const handleSubmit = (event)=>{
    event.preventDefault();
    sendRequest().then(() => history("/login"));
  };

  
  return (

    <div className="main">
        <div className="boxsignup" style={{ padding:"150px"}}>
                        <h1>Sign Up</h1>
                        <p></p>
                        <TextField inputProps={{ sx: {width: '300px'} }}  id="outlined-basic" label="Name" variant="outlined"  onChange={(event)=>{
                        setName(event.target.value);}}/>
                        <p></p>
                        <TextField inputProps={{ sx: {width: '300px'} }}  id="outlined-basic" label="Email" variant="outlined" onChange={(event)=>{
                        setEmail(event.target.value);}}/>
                        <p></p>
                        <TextField inputProps={{ sx: {width: '300px'} }}  id="outlined-basic" label="Password" variant="outlined" onChange={(event)=>{
                        setPassword(event.target.value);}} />
                        <p></p>
                        <FormControl fullWidth>
                            <InputLabel id="demo-simple-select-label">Role</InputLabel>
                            <Select
                                labelId="demo-simple-select-label"
                                id="demo-simple-select"
                                
                                label="Role"
                                onChange={(event)=>{
                                  setRole(event.target.value);}}>
                                  
                                <MenuItem value={"student"}>Student</MenuItem>
                                <MenuItem value={"faculty"}>Faculty</MenuItem>
                                <MenuItem value={"admin"}>Admin</MenuItem>
                            </Select>
                        </FormControl>
                        <p></p>
                        <ThemeProvider theme={theme}>
                        <Button  style={{width: '329px',fontSize:"14px",fontWeight:'600',padding:'12px'}}variant="contained" onClick={handleSubmit}>Signup</Button>
                        </ThemeProvider>
            </div>
    </div>
  )
}

export default Signup
